import Moya

enum GitHubAPI: TargetType {
    
    case allIssues(owner: String, repo: String)
    
    static func createBaseURL() -> URL {
        let urlString = "https://api.github.com"
        guard let url = URL(string: urlString) else { fatalError("The URL '\(urlString)' isn't valid") }
        return url
    }
    
    var baseURL: URL { GitHubAPI.createBaseURL() }
    
    var path: String {
        switch self {
        case let .allIssues(owner, repo):
            return "/repos/\(owner)/\(repo)/issues"
        }
    }
    
    var method: Moya.Method {
        switch self {
        case .allIssues:
            return .get
        }
    }
    
    var task: Task {
        switch self {
        case .allIssues:
            return .requestPlain
        }
    }
    
    var headers: [String : String]? {
        switch self {
        case .allIssues:
            return ["Content-type": "application/json"]
        }
    }
    
    var sampleData: Data {
        switch self {
        case .allIssues(_, _):
            return """
    [{"url":"https://api.github.com/repos/apple/swift/issues/27797","repository_url":"https://api.github.com/repos/apple/swift","labels_url":"https://api.github.com/repos/apple/swift/issues/27797/labels{/name}","comments_url":"https://api.github.com/repos/apple/swift/issues/27797/comments","events_url":"https://api.github.com/repos/apple/swift/issues/27797/events","html_url":"https://github.com/apple/swift/pull/27797","id":509518469,"node_id":"MDExOlB1bGxSZXF1ZXN0MzMwMDY0NDUx","number":27797,"title":"Improve warning for inferring an undesirable type","user":{"login":"jder","id":55957,"node_id":"MDQ6VXNlcjU1OTU3","avatar_url":"https://avatars1.githubusercontent.com/u/55957?v=4","gravatar_id":"","url":"https://api.github.com/users/jder","html_url":"https://github.com/jder","followers_url":"https://api.github.com/users/jder/followers","following_url":"https://api.github.com/users/jder/following{/other_user}","gists_url":"https://api.github.com/users/jder/gists{/gist_id}","starred_url":"https://api.github.com/users/jder/starred{/owner}{/repo}","subscriptions_url":"https://api.github.com/users/jder/subscriptions","organizations_url":"https://api.github.com/users/jder/orgs","repos_url":"https://api.github.com/users/jder/repos","events_url":"https://api.github.com/users/jder/events{/privacy}","received_events_url":"https://api.github.com/users/jder/received_events","type":"User","site_admin":false},"labels":[],"state":"open","locked":false,"assignee":null,"assignees":[],"milestone":null,"comments":0,"created_at":"2019-10-19T22:41:03Z","updated_at":"2019-10-19T23:18:44Z","closed_at":null,"author_association":"CONTRIBUTOR","pull_request":{"url":"https://api.github.com/repos/apple/swift/pulls/27797","html_url":"https://github.com/apple/swift/pull/27797","diff_url":"https://github.com/apple/swift/pull/27797.diff","patch_url":"https://github.com/apple/swift/pull/27797.patch"},"body":"<!-- What's in this pull request? -->\r\nAdds a fixit for the warning about inferring a probably-unexpected type, and adds another case for when inferring a type of `[()]`. It would be easy to also warn for arrays of any of the existing cases but this seemed like overkill and would require some additional messages. \r\n\r\nTaken together, this:\r\n\r\n```swift\r\nlet y = x.map { print($0) }\r\n```\r\n\r\nNow produces:\r\n\r\n```\r\nwarning: variable 'y' inferred to have type '[()]'\r\nadd an explicit type annotation to silence this warning\r\n```\r\nWith a fixit to add `: [()]` after `y`.\r\n\r\n<!-- If this pull request resolves any bugs in the Swift bug tracker, provide a link: -->\r\nResolves [SR-11511](https://bugs.swift.org/browse/SR-11511).\r\n\r\n<!--\r\nBefore merging this pull request, you must run the Swift continuous integration tests.\r\nFor information about triggering CI builds via @swift-ci, see:\r\nhttps://github.com/apple/swift/blob/master/docs/ContinuousIntegration.md#swift-ci\r\n\r\nThank you for your contribution to Swift!\r\n-->\r\n"},{"url":"https://api.github.com/repos/apple/swift/issues/27796","repository_url":"https://api.github.com/repos/apple/swift","labels_url":"https://api.github.com/repos/apple/swift/issues/27796/labels{/name}","comments_url":"https://api.github.com/repos/apple/swift/issues/27796/comments","events_url":"https://api.github.com/repos/apple/swift/issues/27796/events","html_url":"https://github.com/apple/swift/pull/27796","id":509516632,"node_id":"MDExOlB1bGxSZXF1ZXN0MzMwMDYzMTQ2","number":27796,"title":"[AutoDiff] Add loop differentiation negative testcases.","user":{"login":"dan-zheng","id":5590046,"node_id":"MDQ6VXNlcjU1OTAwNDY=","avatar_url":"https://avatars0.githubusercontent.com/u/5590046?v=4","gravatar_id":"","url":"https://api.github.com/users/dan-zheng","html_url":"https://github.com/dan-zheng","followers_url":"https://api.github.com/users/dan-zheng/followers","following_url":"https://api.github.com/users/dan-zheng/following{/other_user}","gists_url":"https://api.github.com/users/dan-zheng/gists{/gist_id}","starred_url":"https://api.github.com/users/dan-zheng/starred{/owner}{/repo}","subscriptions_url":"https://api.github.com/users/dan-zheng/subscriptions","organizations_url":"https://api.github.com/users/dan-zheng/orgs","repos_url":"https://api.github.com/users/dan-zheng/repos","events_url":"https://api.github.com/users/dan-zheng/events{/privacy}","received_events_url":"https://api.github.com/users/dan-zheng/received_events","type":"User","site_admin":false},"labels":[{"id":955546636,"node_id":"MDU6TGFiZWw5NTU1NDY2MzY=","url":"https://api.github.com/repos/apple/swift/labels/tensorflow","name":"tensorflow","color":"EF6C00","default":false}],"state":"open","locked":false,"assignee":null,"assignees":[],"milestone":null,"comments":1,"created_at":"2019-10-19T22:18:54Z","updated_at":"2019-10-19T23:04:13Z","closed_at":null,"author_association":"COLLABORATOR","pull_request":{"url":"https://api.github.com/repos/apple/swift/pulls/27796","html_url":"https://github.com/apple/swift/pull/27796","diff_url":"https://github.com/apple/swift/pull/27796.diff","patch_url":"https://github.com/apple/swift/pull/27796.patch"},"body":"Loop differentiation produces incorrect results when the reduction accumulation\r\nvariable is not initialized with an active parameter, e.g. when using\r\n`var result = 1` instead of `var result = x`.\r\n\r\n```swift\r\nfunc for_loop_nonactive_initial_value(_ x: Float) -> Float {\r\n  var result: Float = 1\r\n  for _ in 0..<2 {\r\n    result = result * x\r\n  }\r\n  return result\r\n}\r\nprint(valueWithGradient(at: 3, in: for_loop_nonactive_initial_value))\r\n//   Actual: (value: 9.0, gradient: 3.0)\r\n// Expected: (value: 9.0, gradient: 6.0)\r\n```\r\n\r\n[TF-933](https://bugs.swift.org/browse/TF-933) tracks this issue. This patch add negative testcases (currently failing).\r\n\r\n---\r\n\r\nWorkarounds are to reformulate using `var result = x` or by using\r\n`Array.differentiableReduce`:\r\n\r\n```swift\r\n// Workaround 1: use `var result = x` instead of `var result = 1`.\r\nfunc for_loop_active_initial_value(_ x: Float) -> Float {\r\n  var result = x\r\n  for _ in 0..<1 {\r\n    result = result * x\r\n  }\r\n  return result\r\n}\r\nprint(valueWithGradient(at: 3, in: for_loop_active_initial_value))\r\n// (value: 9.0, gradient: 6.0)\r\n```\r\n\r\n```swift\r\n// Workaround 2: use `Array.differentiableReduce`.\r\nfunc power(_ x: Double, _ exponent: Int) -> Double {\r\n  // Array allocation isn't efficient, but `Array.differentiableReduce` has correct derivatives.\r\n  let array = Array(repeating: x, count: exponent)\r\n  return array.differentiableReduce(1, *)\r\n}\r\nprint(valueWithGradient(at: 3, in: { x in power(x, 2) }))\r\n// (value: 9.0, gradient: 6.0)\r\n```"},{"url":"https://api.github.com/repos/apple/swift/issues/27795","repository_url":"https://api.github.com/repos/apple/swift","labels_url":"https://api.github.com/repos/apple/swift/issues/27795/labels{/name}","comments_url":"https://api.github.com/repos/apple/swift/issues/27795/comments","events_url":"https://api.github.com/repos/apple/swift/issues/27795/events","html_url":"https://github.com/apple/swift/pull/27795","id":509511099,"node_id":"MDExOlB1bGxSZXF1ZXN0MzMwMDU5MTk1","number":27795,"title":"[Diagnostics] Move macros used in diagnostic definitions to their own header","user":{"login":"owenv","id":1946221,"node_id":"MDQ6VXNlcjE5NDYyMjE=","avatar_url":"https://avatars0.githubusercontent.com/u/1946221?v=4","gravatar_id":"","url":"https://api.github.com/users/owenv","html_url":"https://github.com/owenv","followers_url":"https://api.github.com/users/owenv/followers","following_url":"https://api.github.com/users/owenv/following{/other_user}","gists_url":"https://api.github.com/users/owenv/gists{/gist_id}","starred_url":"https://api.github.com/users/owenv/starred{/owner}{/repo}","subscriptions_url":"https://api.github.com/users/owenv/subscriptions","organizations_url":"https://api.github.com/users/owenv/orgs","repos_url":"https://api.github.com/users/owenv/repos","events_url":"https://api.github.com/users/owenv/events{/privacy}","received_events_url":"https://api.github.com/users/owenv/received_events","type":"User","site_admin":false},"labels":[],"state":"open","locked":false,"assignee":null,"assignees":[],"milestone":null,"comments":0,"created_at":"2019-10-19T21:19:54Z","updated_at":"2019-10-19T21:20:52Z","closed_at":null,"author_association":"CONTRIBUTOR","pull_request":{"url":"https://api.github.com/repos/apple/swift/pulls/27795","html_url":"https://github.com/apple/swift/pull/27795","diff_url":"https://github.com/apple/swift/pull/27795.diff","patch_url":"https://github.com/apple/swift/pull/27795.patch"},"body":"These were duplicated in 11 different files, and as they've gotten more complex a few inconsistencies have snuck in. Sharing them should make future changes easier and less bug-prone.\r\n"},{"url":"https://api.github.com/repos/apple/swift/issues/27794","repository_url":"https://api.github.com/repos/apple/swift","labels_url":"https://api.github.com/repos/apple/swift/issues/27794/labels{/name}","comments_url":"https://api.github.com/repos/apple/swift/issues/27794/comments","events_url":"https://api.github.com/repos/apple/swift/issues/27794/events","html_url":"https://github.com/apple/swift/pull/27794","id":509504466,"node_id":"MDExOlB1bGxSZXF1ZXN0MzMwMDU0MTMy","number":27794,"title":"[SIL Optimization] Factor out a utility function: isConsumingUse from CopyPropagation.cpp into InstOptUtils.cpp.","user":{"login":"ravikandhadai","id":37849400,"node_id":"MDQ6VXNlcjM3ODQ5NDAw","avatar_url":"https://avatars3.githubusercontent.com/u/37849400?v=4","gravatar_id":"","url":"https://api.github.com/users/ravikandhadai","html_url":"https://github.com/ravikandhadai","followers_url":"https://api.github.com/users/ravikandhadai/followers","following_url":"https://api.github.com/users/ravikandhadai/following{/other_user}","gists_url":"https://api.github.com/users/ravikandhadai/gists{/gist_id}","starred_url":"https://api.github.com/users/ravikandhadai/starred{/owner}{/repo}","subscriptions_url":"https://api.github.com/users/ravikandhadai/subscriptions","organizations_url":"https://api.github.com/users/ravikandhadai/orgs","repos_url":"https://api.github.com/users/ravikandhadai/repos","events_url":"https://api.github.com/users/ravikandhadai/events{/privacy}","received_events_url":"https://api.github.com/users/ravikandhadai/received_events","type":"User","site_admin":false},"labels":[],"state":"open","locked":false,"assignee":null,"assignees":[],"milestone":null,"comments":1,"created_at":"2019-10-19T20:15:46Z","updated_at":"2019-10-19T20:47:25Z","closed_at":null,"author_association":"CONTRIBUTOR","pull_request":{"url":"https://api.github.com/repos/apple/swift/pulls/27794","html_url":"https://github.com/apple/swift/pull/27794","diff_url":"https://github.com/apple/swift/pull/27794.diff","patch_url":"https://github.com/apple/swift/pull/27794.patch"},"body":"The function checks if a given use of a non-trivial, non-address SILValue will consume it\r\ni.e., decrease its reference count.\r\n\r\nThis function will be used by the OSLogOptimization pass while constant folding values. See this PR: https://github.com/apple/swift/pull/27744, function `replaceAllUsesAndFixLifetimes`. "},{"url":"https://api.github.com/repos/apple/swift/issues/27793","repository_url":"https://api.github.com/repos/apple/swift","labels_url":"https://api.github.com/repos/apple/swift/issues/27793/labels{/name}","comments_url":"https://api.github.com/repos/apple/swift/issues/27793/comments","events_url":"https://api.github.com/repos/apple/swift/issues/27793/events","html_url":"https://github.com/apple/swift/pull/27793","id":509489688,"node_id":"MDExOlB1bGxSZXF1ZXN0MzMwMDQzODQ4","number":27793,"title":"Back port foundation tests","user":{"login":"phausler","id":278470,"node_id":"MDQ6VXNlcjI3ODQ3MA==","avatar_url":"https://avatars1.githubusercontent.com/u/278470?v=4","gravatar_id":"","url":"https://api.github.com/users/phausler","html_url":"https://github.com/phausler","followers_url":"https://api.github.com/users/phausler/followers","following_url":"https://api.github.com/users/phausler/following{/other_user}","gists_url":"https://api.github.com/users/phausler/gists{/gist_id}","starred_url":"https://api.github.com/users/phausler/starred{/owner}{/repo}","subscriptions_url":"https://api.github.com/users/phausler/subscriptions","organizations_url":"https://api.github.com/users/phausler/orgs","repos_url":"https://api.github.com/users/phausler/repos","events_url":"https://api.github.com/users/phausler/events{/privacy}","received_events_url":"https://api.github.com/users/phausler/received_events","type":"User","site_admin":false},"labels":[],"state":"open","locked":false,"assignee":null,"assignees":[],"milestone":null,"comments":1,"created_at":"2019-10-19T18:13:11Z","updated_at":"2019-10-19T18:13:29Z","closed_at":null,"author_association":"MEMBER","pull_request":{"url":"https://api.github.com/repos/apple/swift/pulls/27793","html_url":"https://github.com/apple/swift/pull/27793","diff_url":"https://github.com/apple/swift/pull/27793.diff","patch_url":"https://github.com/apple/swift/pull/27793.patch"},"body":"The test suite for swift's overlay fails to run when being tested pre-macOS10.15/iOS13. This is caused because of a pair of behavioral fixes that were applied expecting the underlying types to adhere to the expected characteristics. That makes testing in those scenarios invalid and report failure. This allows the tests to be adequately run on previous releases."},{"url":"https://api.github.com/repos/apple/swift/issues/27792","repository_url":"https://api.github.com/repos/apple/swift","labels_url":"https://api.github.com/repos/apple/swift/issues/27792/labels{/name}","comments_url":"https://api.github.com/repos/apple/swift/issues/27792/comments","events_url":"https://api.github.com/repos/apple/swift/issues/27792/events","html_url":"https://github.com/apple/swift/pull/27792","id":509484969,"node_id":"MDExOlB1bGxSZXF1ZXN0MzMwMDQwODM1","number":27792,"title":"Popcnt optimization","user":{"login":"zoecarver","id":13806068,"node_id":"MDQ6VXNlcjEzODA2MDY4","avatar_url":"https://avatars1.githubusercontent.com/u/13806068?v=4","gravatar_id":"","url":"https://api.github.com/users/zoecarver","html_url":"https://github.com/zoecarver","followers_url":"https://api.github.com/users/zoecarver/followers","following_url":"https://api.github.com/users/zoecarver/following{/other_user}","gists_url":"https://api.github.com/users/zoecarver/gists{/gist_id}","starred_url":"https://api.github.com/users/zoecarver/starred{/owner}{/repo}","subscriptions_url":"https://api.github.com/users/zoecarver/subscriptions","organizations_url":"https://api.github.com/users/zoecarver/orgs","repos_url":"https://api.github.com/users/zoecarver/repos","events_url":"https://api.github.com/users/zoecarver/events{/privacy}","received_events_url":"https://api.github.com/users/zoecarver/received_events","type":"User","site_admin":false},"labels":[],"state":"open","locked":false,"assignee":null,"assignees":[],"milestone":null,"comments":0,"created_at":"2019-10-19T17:37:02Z","updated_at":"2019-10-19T17:37:50Z","closed_at":null,"author_association":"CONTRIBUTOR","pull_request":{"url":"https://api.github.com/repos/apple/swift/pulls/27792","html_url":"https://github.com/apple/swift/pull/27792","diff_url":"https://github.com/apple/swift/pull/27792.diff","patch_url":"https://github.com/apple/swift/pull/27792.patch"},"body":"This patch optimizes count-set-bit functions into a call to LLVM's `popcnt` intrinsic. This is not only good for binary size but, also improves performance. \r\n\r\nTo enable the best result, we have to tell llvm the target architecture. If we do this, then (on CPUs that support it) the call to the `popcnt` intrinsic can be optimized to a single `popcnt` instruction. \r\n\r\nHere are some examples (notice, no jumps and no branches):\r\n### Input 1\r\n```swift\r\nfunc test(_ _x: Int) -> Int {\r\n    var x = _x\r\n    var c = Int()\r\n    while x != 0 {\r\n        c += 1;\r\n        x &= (x - 1);\r\n    }\r\n    return c\r\n}\r\n```\r\n\r\n### New output\r\n```\r\n_$s3run4testyS2iF:\r\n\tpushq\t%rbp\r\n\tmovq\t%rsp, %rbp\r\n\tmovq\t%rdi, %rax\r\n\tshrq\t%rax\r\n\tmovabsq\t$6148914691236517205, %rcx\r\n\tandq\t%rax, %rcx\r\n\tsubq\t%rcx, %rdi\r\n\tmovabsq\t$3689348814741910323, %rax\r\n\tmovq\t%rdi, %rcx\r\n\tandq\t%rax, %rcx\r\n\tshrq\t$2, %rdi\r\n\tandq\t%rax, %rdi\r\n\taddq\t%rcx, %rdi\r\n\tmovq\t%rdi, %rax\r\n\tshrq\t$4, %rax\r\n\tleaq\t(%rax,%rdi), %rax\r\n\tmovabsq\t$1085102592571150095, %rcx\r\n\tandq\t%rax, %rcx\r\n\tmovabsq\t$72340172838076673, %rax\r\n\timulq\t%rcx, %rax\r\n\tshrq\t$56, %rax\r\n\tpopq\t%rbp\r\n\tretq\r\n```\r\n\r\n### New output (haswell and later, not tested)\r\n```\r\n_$s3run4testyS2iF:\r\n\tpopcnt  %rax, %rdi\r\n\tretq\r\n```\r\n\r\n### Old output\r\n```\r\n_$s3run4testyS2iF:\r\n\tpushq\t%rbp\r\n\tmovq\t%rsp, %rbp\r\n\ttestq\t%rdi, %rdi\r\n\tje\tLBB1_1\r\n\txorl\t%ecx, %ecx\r\n\t.p2align\t4, 0x90\r\nLBB1_3:\r\n\tmovq\t%rcx, %rax\r\n\tincq\t%rax\r\n\tjo\tLBB1_7\r\n\tmovq\t%rdi, %rdx\r\n\tdecq\t%rdx\r\n\tjo\tLBB1_8\r\n\tincq\t%rcx\r\n\tandq\t%rdx, %rdi\r\n\tjne\tLBB1_3\r\n\tpopq\t%rbp\r\n\tretq\r\nLBB1_1:\r\n\txorl\t%eax, %eax\r\n\tpopq\t%rbp\r\n\tretq\r\nLBB1_7:\r\n\t## InlineAsm Start\r\n\t## InlineAsm End\r\n\tud2\r\nLBB1_8:\r\n\t## InlineAsm Start\r\n\t## InlineAsm End\r\n\tud2\r\n```\r\n\r\n### Input 2\r\n```swift\r\nfunc test(_ _x: Int) -> Int {\r\n    var x = _x\r\n    var c = Int()\r\n    var o = 0\r\n    while x != 0 {\r\n        c += 1;\r\n        o += c;\r\n        x &= (x - 1);\r\n    }\r\n    return c - o\r\n}\r\n```\r\n\r\n### New output\r\n```\r\n_$s3run4testyS2iF:\r\n\tpushq\t%rbp\r\n\tmovq\t%rsp, %rbp\r\n\txorl\t%eax, %eax\r\n\tpopq\t%rbp\r\n\tretq\r\n```\r\n\r\n### Old output\r\n```\r\n_$s3run4testyS2iF:\r\n\tpushq\t%rbp\r\n\tmovq\t%rsp, %rbp\r\n\ttestq\t%rdi, %rdi\r\n\tje\tLBB1_1\r\n\txorl\t%edx, %edx\r\n\txorl\t%ecx, %ecx\r\n\t.p2align\t4, 0x90\r\nLBB1_3:\r\n\tmovq\t%rdx, %rax\r\n\tincq\t%rax\r\n\tjo\tLBB1_9\r\n\taddq\t%rax, %rcx\r\n\tjo\tLBB1_10\r\n\tmovq\t%rdi, %rsi\r\n\tdecq\t%rsi\r\n\tjo\tLBB1_11\r\n\tincq\t%rdx\r\n\tandq\t%rsi, %rdi\r\n\tjne\tLBB1_3\r\n\tsubq\t%rcx, %rax\r\n\tjo\tLBB1_12\r\nLBB1_8:\r\n\tpopq\t%rbp\r\n\tretq\r\nLBB1_1:\r\n\txorl\t%eax, %eax\r\n\txorl\t%ecx, %ecx\r\n\tsubq\t%rcx, %rax\r\n\tjno\tLBB1_8\r\nLBB1_12:\r\n\t## InlineAsm Start\r\n\t## InlineAsm End\r\n\tud2\r\nLBB1_10:\r\n\t## InlineAsm Start\r\n\t## InlineAsm End\r\n\tud2\r\nLBB1_11:\r\n\t## InlineAsm Start\r\n\t## InlineAsm End\r\n\tud2\r\nLBB1_9:\r\n\t## InlineAsm Start\r\n\t## InlineAsm End\r\n\tud2\r\n```\r\n\r\nThis patch is _almost_ ready for @swift-ci to test and benchmark (I'm not sure we'll see a huge improvement, though). I sill need to:\r\n\r\n - [ ] fix the issue with loops\r\n - [ ] tell llvm the architecture (this should probably be its own patch, though)\r\n"},{"url":"https://api.github.com/repos/apple/swift/issues/27791","repository_url":"https://api.github.com/repos/apple/swift","labels_url":"https://api.github.com/repos/apple/swift/issues/27791/labels{/name}","comments_url":"https://api.github.com/repos/apple/swift/issues/27791/comments","events_url":"https://api.github.com/repos/apple/swift/issues/27791/events","html_url":"https://github.com/apple/swift/pull/27791","id":509480490,"node_id":"MDExOlB1bGxSZXF1ZXN0MzMwMDM3OTY5","number":27791,"title":"[update-checkout] Error message if monorepo symlinks cannot be created","user":{"login":"ahoppen","id":4062178,"node_id":"MDQ6VXNlcjQwNjIxNzg=","avatar_url":"https://avatars3.githubusercontent.com/u/4062178?v=4","gravatar_id":"","url":"https://api.github.com/users/ahoppen","html_url":"https://github.com/ahoppen","followers_url":"https://api.github.com/users/ahoppen/followers","following_url":"https://api.github.com/users/ahoppen/following{/other_user}","gists_url":"https://api.github.com/users/ahoppen/gists{/gist_id}","starred_url":"https://api.github.com/users/ahoppen/starred{/owner}{/repo}","subscriptions_url":"https://api.github.com/users/ahoppen/subscriptions","organizations_url":"https://api.github.com/users/ahoppen/orgs","repos_url":"https://api.github.com/users/ahoppen/repos","events_url":"https://api.github.com/users/ahoppen/events{/privacy}","received_events_url":"https://api.github.com/users/ahoppen/received_events","type":"User","site_admin":false},"labels":[],"state":"open","locked":false,"assignee":null,"assignees":[],"milestone":null,"comments":2,"created_at":"2019-10-19T17:02:37Z","updated_at":"2019-10-19T17:51:38Z","closed_at":null,"author_association":"COLLABORATOR","pull_request":{"url":"https://api.github.com/repos/apple/swift/pulls/27791","html_url":"https://github.com/apple/swift/pull/27791","diff_url":"https://github.com/apple/swift/pull/27791.diff","patch_url":"https://github.com/apple/swift/pull/27791.patch"},"body":"If `update-checkout` is trying to create the symlinks to the monorepo but the projects are already checked out, it fails with a pretty ambiguous `OSError: [Errno 17] File exists`. This adds a nicer error message, telling you what happened and what to do: \r\n\r\n```\r\nFile '/Users/alex/swift-src/clang' already exists. Remove it, so update-checkout can create the symlink to the llvm-monorepo.\r\n```"},{"url":"https://api.github.com/repos/apple/swift/issues/27789","repository_url":"https://api.github.com/repos/apple/swift","labels_url":"https://api.github.com/repos/apple/swift/issues/27789/labels{/name}","comments_url":"https://api.github.com/repos/apple/swift/issues/27789/comments","events_url":"https://api.github.com/repos/apple/swift/issues/27789/events","html_url":"https://github.com/apple/swift/pull/27789","id":509367073,"node_id":"MDExOlB1bGxSZXF1ZXN0MzI5OTY2ODA1","number":27789,"title":"[SyntaxParse] Assert that the children of syntax tree nodes have contiguous ranges","user":{"login":"rintaro","id":142081,"node_id":"MDQ6VXNlcjE0MjA4MQ==","avatar_url":"https://avatars1.githubusercontent.com/u/142081?v=4","gravatar_id":"","url":"https://api.github.com/users/rintaro","html_url":"https://github.com/rintaro","followers_url":"https://api.github.com/users/rintaro/followers","following_url":"https://api.github.com/users/rintaro/following{/other_user}","gists_url":"https://api.github.com/users/rintaro/gists{/gist_id}","starred_url":"https://api.github.com/users/rintaro/starred{/owner}{/repo}","subscriptions_url":"https://api.github.com/users/rintaro/subscriptions","organizations_url":"https://api.github.com/users/rintaro/orgs","repos_url":"https://api.github.com/users/rintaro/repos","events_url":"https://api.github.com/users/rintaro/events{/privacy}","received_events_url":"https://api.github.com/users/rintaro/received_events","type":"User","site_admin":false},"labels":[],"state":"open","locked":false,"assignee":null,"assignees":[],"milestone":null,"comments":1,"created_at":"2019-10-19T00:53:08Z","updated_at":"2019-10-19T00:54:44Z","closed_at":null,"author_association":"MEMBER","pull_request":{"url":"https://api.github.com/repos/apple/swift/pulls/27789","html_url":"https://github.com/apple/swift/pull/27789","diff_url":"https://github.com/apple/swift/pull/27789.diff","patch_url":"https://github.com/apple/swift/pull/27789.patch"},"body":"Re-apply #27406 by @nathawes \r\nAlso apply the assertion to node creations from `SyntaxParsingContext::createSyntaxAs()` API.\r\n\r\n(Disabled syntax tree creation for `syntax-ide-test -code-completion` because code completion skips some portion of the source text)"},{"url":"https://api.github.com/repos/apple/swift/issues/27785","repository_url":"https://api.github.com/repos/apple/swift","labels_url":"https://api.github.com/repos/apple/swift/issues/27785/labels{/name}","comments_url":"https://api.github.com/repos/apple/swift/issues/27785/comments","events_url":"https://api.github.com/repos/apple/swift/issues/27785/events","html_url":"https://github.com/apple/swift/pull/27785","id":509357365,"node_id":"MDExOlB1bGxSZXF1ZXN0MzI5OTU5ODgw","number":27785,"title":"[windows] Provide a symlink alternative for Windows.","user":{"login":"drodriguez","id":6338,"node_id":"MDQ6VXNlcjYzMzg=","avatar_url":"https://avatars0.githubusercontent.com/u/6338?v=4","gravatar_id":"","url":"https://api.github.com/users/drodriguez","html_url":"https://github.com/drodriguez","followers_url":"https://api.github.com/users/drodriguez/followers","following_url":"https://api.github.com/users/drodriguez/following{/other_user}","gists_url":"https://api.github.com/users/drodriguez/gists{/gist_id}","starred_url":"https://api.github.com/users/drodriguez/starred{/owner}{/repo}","subscriptions_url":"https://api.github.com/users/drodriguez/subscriptions","organizations_url":"https://api.github.com/users/drodriguez/orgs","repos_url":"https://api.github.com/users/drodriguez/repos","events_url":"https://api.github.com/users/drodriguez/events{/privacy}","received_events_url":"https://api.github.com/users/drodriguez/received_events","type":"User","site_admin":false},"labels":[],"state":"open","locked":false,"assignee":null,"assignees":[],"milestone":null,"comments":3,"created_at":"2019-10-18T23:52:29Z","updated_at":"2019-10-19T00:28:03Z","closed_at":null,"author_association":"COLLABORATOR","pull_request":{"url":"https://api.github.com/repos/apple/swift/pulls/27785","html_url":"https://github.com/apple/swift/pull/27785","diff_url":"https://github.com/apple/swift/pull/27785.diff","patch_url":"https://github.com/apple/swift/pull/27785.patch"},"body":"Python 2.7 in Windows do not have os.symlink. Provide one by using Win32\r\nAPI, and try to match the behaviour that seems to be implied by the Unix\r\nimplementation (meaning, the destination is removed before the symlink\r\nis created).\r\n\r\nBefore this change, calling update-checkout.cmd in Windows will have failed when os.symlink was used.\r\n\r\nThis is not used by the build script at the moment, but people might use the script in Windows to update their local copies. Sadly creating symlinks in Windows require the user to be Administrators, but that's also a requirement for adapting the Windows SDK to be used modularly, so developers should be already setup."},{"url":"https://api.github.com/repos/apple/swift/issues/27784","repository_url":"https://api.github.com/repos/apple/swift","labels_url":"https://api.github.com/repos/apple/swift/issues/27784/labels{/name}","comments_url":"https://api.github.com/repos/apple/swift/issues/27784/comments","events_url":"https://api.github.com/repos/apple/swift/issues/27784/events","html_url":"https://github.com/apple/swift/pull/27784","id":509356073,"node_id":"MDExOlB1bGxSZXF1ZXN0MzI5OTU4OTQ2","number":27784,"title":"[Serialization] Store whether an override depends on its base for ABI","user":{"login":"jrose-apple","id":15097531,"node_id":"MDQ6VXNlcjE1MDk3NTMx","avatar_url":"https://avatars2.githubusercontent.com/u/15097531?v=4","gravatar_id":"","url":"https://api.github.com/users/jrose-apple","html_url":"https://github.com/jrose-apple","followers_url":"https://api.github.com/users/jrose-apple/followers","following_url":"https://api.github.com/users/jrose-apple/following{/other_user}","gists_url":"https://api.github.com/users/jrose-apple/gists{/gist_id}","starred_url":"https://api.github.com/users/jrose-apple/starred{/owner}{/repo}","subscriptions_url":"https://api.github.com/users/jrose-apple/subscriptions","organizations_url":"https://api.github.com/users/jrose-apple/orgs","repos_url":"https://api.github.com/users/jrose-apple/repos","events_url":"https://api.github.com/users/jrose-apple/events{/privacy}","received_events_url":"https://api.github.com/users/jrose-apple/received_events","type":"User","site_admin":false},"labels":[],"state":"open","locked":false,"assignee":null,"assignees":[],"milestone":null,"comments":3,"created_at":"2019-10-18T23:45:48Z","updated_at":"2019-10-19T00:35:36Z","closed_at":null,"author_association":"MEMBER","pull_request":{"url":"https://api.github.com/repos/apple/swift/pulls/27784","html_url":"https://github.com/apple/swift/pull/27784","diff_url":"https://github.com/apple/swift/pull/27784.diff","patch_url":"https://github.com/apple/swift/pull/27784.patch"},"body":"In some circumstances, a Swift declaration in module A will depend on another declaration (usually from Objective-C) that can't be loaded, for whatever reason. If the Swift declaration is *overriding* the missing declaration, this can present a problem, because the way methods are dispatched in Swift can depend on knowing the original class was that introduced the method. However, if the compiler can prove that the override can still be safely invoked/used in all cases, it doesn't need to worry about the overridden declaration being missing.\r\n\r\nThis is especially relevant for property accessors, because there's currently no logic to recover from a property being successfully deserialized and then finding out that an accessor couldn't be.\r\n\r\nThe decision of whether or not an override can be safely invoked without knowledge of the base method is something to be cautious about—a mistaken analysis would effectively be a miscompile. So up until now, this was limited to one case: when a method is known to be `@objc dynamic`, i.e. always dispatched through objc_msgSend. (Even this may become questionable if we have first-class method references, like we do for key paths.) This worked particularly well because the compiler infers `dynamic` for any overload of an imported Objective-C method or accessor, in case it imports differently in a different -swift-version and a client ends up subclassing it.\r\n\r\nHowever...that inference does not apply if the class is final, because then there are no subclasses to worry about.\r\n\r\nThis commit changes the test to be more careful: if the *missing* declaration was `@objc dynamic`, we know that it can't affect ABI, because either the override is properly `@objc dynamic` as well, or the override has introduced its own calling ABI (in practice, a direct call for final methods) that doesn't depend on the superclass. Again, this isn't 100% correct in the presence of first-class methods, but it does fix the issue in practice where a property accessor in a parent class goes missing. And since Objective-C allows adding property setters separately from the original property declaration, that's something that can happen even under normal circumstances. Sadly.\r\n\r\nThis approach could probably be extended to constructors as well. I'm a little more cautious about throwing vars and subscripts into the mix because of the presence of key paths, which do allow identity-based comparison of overrides and bases.\r\n\r\nrdar://problem/56388950"},{"url":"https://api.github.com/repos/apple/swift/issues/27776","repository_url":"https://api.github.com/repos/apple/swift","labels_url":"https://api.github.com/repos/apple/swift/issues/27776/labels{/name}","comments_url":"https://api.github.com/repos/apple/swift/issues/27776/comments","events_url":"https://api.github.com/repos/apple/swift/issues/27776/events","html_url":"https://github.com/apple/swift/pull/27776","id":509234609,"node_id":"MDExOlB1bGxSZXF1ZXN0MzI5ODY0NTk4","number":27776,"title":"[requires-evolution] Support multiple patterns in catch clauses","user":{"login":"owenv","id":1946221,"node_id":"MDQ6VXNlcjE5NDYyMjE=","avatar_url":"https://avatars0.githubusercontent.com/u/1946221?v=4","gravatar_id":"","url":"https://api.github.com/users/owenv","html_url":"https://github.com/owenv","followers_url":"https://api.github.com/users/owenv/followers","following_url":"https://api.github.com/users/owenv/following{/other_user}","gists_url":"https://api.github.com/users/owenv/gists{/gist_id}","starred_url":"https://api.github.com/users/owenv/starred{/owner}{/repo}","subscriptions_url":"https://api.github.com/users/owenv/subscriptions","organizations_url":"https://api.github.com/users/owenv/orgs","repos_url":"https://api.github.com/users/owenv/repos","events_url":"https://api.github.com/users/owenv/events{/privacy}","received_events_url":"https://api.github.com/users/owenv/received_events","type":"User","site_admin":false},"labels":[],"state":"open","locked":false,"assignee":null,"assignees":[],"milestone":null,"comments":0,"created_at":"2019-10-18T18:06:56Z","updated_at":"2019-10-18T18:06:56Z","closed_at":null,"author_association":"CONTRIBUTOR","pull_request":{"url":"https://api.github.com/repos/apple/swift/pulls/27776","html_url":"https://github.com/apple/swift/pull/27776","diff_url":"https://github.com/apple/swift/pull/27776.diff","patch_url":"https://github.com/apple/swift/pull/27776.patch"},"body":"e.g. `do { ... } catch MyErr.a(let code), MyErr.b(let code) { ... }`\r\n\r\nThe approach I took here was to get rid of `CatchStmt` entirely and reuse `CaseStmt` in `DoCatchStmt`. If anyone has time to take a look, I'd appreciate feedback on whether this seems like a good approach before I pitch this on the forums. Initially I tried keeping `CatchStmt` around and just modifying it to hold `CaseLabelItems`, but it resulted in a lot of code duplication in Sema and SILGen. This approach is cleaner overall, but there are still some rough edges (in the profiling code, for instance).\r\n\r\nTODO:\r\n- A couple of code completion tests are still failing\r\n- SILGen tests\r\n- See if there are any more opportunities to reduce code duplication between switch and do-catch statements\r\n- I may implement `#if` support for catch clausesl as part of the eventual proposal, but that'll probably be a separate patch."},{"url":"https://api.github.com/repos/apple/swift/issues/27762","repository_url":"https://api.github.com/repos/apple/swift","labels_url":"https://api.github.com/repos/apple/swift/issues/27762/labels{/name}","comments_url":"https://api.github.com/repos/apple/swift/issues/27762/comments","events_url":"https://api.github.com/repos/apple/swift/issues/27762/events","html_url":"https://github.com/apple/swift/pull/